import React, { useState } from "react";

function Board({ squares, onPlay }) {
  // Chúng ta sẽ nhận `squares` và `onPlay` từ `Game` qua props.
  function renderSquare(i) {
    return (
      <button
        className="square"
        onClick={() => {
          if (squares[i] || calculateWinner(squares)) return; // Ngăn chặn nước đi sai.
          const nextSquares = squares.slice();
          nextSquares[i] = onPlay ? "X" : "O";
          onPlay(nextSquares);
        }}
      >
        {squares[i]}
      </button>
    );
  }

  return (
    <div>
      <div className="board-row">
        {renderSquare(0)}
        {renderSquare(1)}
        {renderSquare(2)}
      </div>
      <div className="board-row">
        {renderSquare(3)}
        {renderSquare(4)}
        {renderSquare(5)}
      </div>
      <div className="board-row">
        {renderSquare(6)}
        {renderSquare(7)}
        {renderSquare(8)}
      </div>
    </div>
  );
}

export default function Game() {
  const [xIsNext, setXIsNext] = useState(true); // Trạng thái xác định người chơi tiếp theo.
  const [history, setHistory] = useState([Array(9).fill(null)]); // Lịch sử các trạng thái bàn cờ.

  return (
    <div className="game">
      <div className="game-board">
        <Board
          squares={history[history.length - 1]}
          onPlay={(nextSquares) => {
            const newHistory = [...history, nextSquares];
            setHistory(newHistory);
            setXIsNext(!xIsNext);
          }}
        />
      </div>
      <div className="game-info">
        <ol>{/* TODO: Hiển thị lịch sử các nước đi */}</ol>
      </div>
    </div>
  );
}

// Hàm kiểm tra người thắng (cần thêm vào file nếu chưa có).
function calculateWinner(squares) {
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6],
  ];
  for (let i = 0; i < lines.length; i++) {
    const [a, b, c] = lines[i];
    if (squares[a] && squares[a] === squares[b] && squares[a] === squares[c]) {
      return squares[a];
    }
  }
  return null;
}
