import { useState } from "react";
function Winner(squares,HL,cHL){
  const lines = [
    [0, 1, 2],
    [3, 4, 5],
    [6, 7, 8],
    [0, 3, 6],
    [1, 4, 7],
    [2, 5, 8],
    [0, 4, 8],
    [2, 4, 6]
  ];
  for(let i=0; i<lines.length; i++){
    const [x,y,z] = lines[i];
    if(!squares[x] || !squares[y] || !squares[z]) continue;
    if(squares[x]==squares[y] && squares[y]==squares[z]){
      const newHL=HL.slice();
      newHL[x]=1;
      newHL[y]=1;
      newHL[z]=1;
      cHL(newHL);
      return squares[x];
    }
  }
  return null;
}
function Square({value,onSquareClick,flag}) {
  console.log(flag);
  return (
    <button className={flag===null ? "square" : "HLsquare"} onClick={onSquareClick}>
      {value}
    </button>
  );
}
function Board({squares,X,onPlay,HLsquares,changeHL}) {
  const winner=Winner(squares,HLsquares,changeHL);
  let status;
  if (winner) {
    status = "Winner: " + winner;
  } else {
    status = "Next player: " + (X ? "X" : "O");
  }
  function handleClick(i){
    if(squares[i] || winner) return;
    const _squares=squares.slice();
    if(X){
      _squares[i]="X";
    }else{
      _squares[i]="O";
    }
    onPlay(_squares);
  }
  const n=3;
  return (
    <>
      <div className="status">{status}</div>
      {Array(n).fill(null).map((_, i) => (
        <div className="board-row" key={i}>
          {Array(n).fill(null).map((_, j) => {
            const id = i*n+j;
            return (
              <Square
                value={squares[id]}
                onSquareClick={() => handleClick(id)}
                flag={HLsquares[id]}
              />
            );
          })}
        </div>
      ))}
    </>
  );
}
export default function Game() {
  const [curX, setX] = useState(true);
  const [history, setHistory] = useState([Array(9).fill(null)]);
  const [HLsquares, setHLsquares] = useState([Array(9).fill(null)]);
  const currentSquares = history[history.length - 1];
  const curHL=HLsquares[0];
  function handlePlay(newsquare){
    setHistory([...history,newsquare]);
    setX(!curX);
  };
  function handleUndo() {
    setHistory((history) => history.slice(0, -1)); 
    setHLsquares([Array(9).fill(null)])
    setX(!curX);
  };
  function handleHL(newHL){
    setHLsquares([newHL]);
  };
  const undo_button = () => {
    let description='Undo';
    return (
      <div>
        <button onClick={() => handleUndo()}>{description}</button>
      </div>
    );
  };
  return (
    <>
      <div className="game">
        <div className="game-board">
          <Board 
            squares={currentSquares} 
            X={curX} 
            onPlay={handlePlay}
            HLsquares={curHL}
            changeHL={handleHL}
          />
        </div>
        <div className="game-info">
          {history.length===1 ? null : <ol>{undo_button()}</ol>}
        </div>
      </div>
    </>
  );
}
